    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        while(!(Validacoes.ValidarCampo(txtNome.getText().trim()) && 
                Validacoes.ValidarCampo(txtCpf.getText().replace(".", "").replace("-", "").trim()) &&
                Validacoes.ValidarCampo(txtEndereco.getText().trim()))){
            txtNome.setBackground(Color.red);
            txtCpf.setBackground(Color.red);
            txtEndereco.setBackground(Color.red);
            PainelGrupoSexo.setBackground(Color.red);
            return;
        }
        
        String nome = txtNome.getText();
        String cpf = txtCpf.getText();
        String telefone = txtTel.getText();
        String email = txtEmail.getText();
        String endereco = txtEndereco.getText();
        String sexo = GrupoSexo.getSelection().getActionCommand();
        
        if(ClienteController.Salvar(nome, cpf, telefone, email, endereco, sexo) == true){
            JOptionPane.showMessageDialog(null, "Cliente cadastrado com sucesso", "Sucesso", JOptionPane.INFORMATION_MESSAGE);
            dispose();
        }else{
            JOptionPane.showMessageDialog(null, "Erro ao cadastrar o cliente tente novamente", "Erro", JOptionPane.ERROR_MESSAGE);
        }
    }//GEN-LAST:event_jButton1ActionPerformed

