    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        if(PesquisaNome.isSelected() && !(txtNome.equals(""))){
            ArrayList<Produto> listaProdutos = ProdutoController.ConsultaProdutoNome(txtNome.getText());
            
            DefaultTableModel modelTabelProduto = new DefaultTableModel();
            modelTabelProduto.addColumn("Cod");
            modelTabelProduto.addColumn("Nome");
            modelTabelProduto.addColumn("Preço");
            modelTabelProduto.addColumn("Quantidade em estoque");
            TabelaProduto.setModel(modelTabelProduto);

            modelTabelProduto.setRowCount(0);

            for (Produto p : listaProdutos) {
                modelTabelProduto.addRow(new Object[]{p.getCod(),p.getNome(),p.getPreco(), EstoqueController.PesquisaEstoque(p.getCod())});
            }

            txtNome.setText("");
            txtCod.setText("");             
        }else if(PesquisaCod.isSelected() && !(txtCod.equals(""))){
           
            ArrayList<Produto> listaProdutos = ProdutoController.ConsultaProdutoCod(txtCod.getText());
            
            DefaultTableModel modelTabelProduto = new DefaultTableModel();
            modelTabelProduto.addColumn("Cod");
            modelTabelProduto.addColumn("Nome");
            modelTabelProduto.addColumn("Preço");
            modelTabelProduto.addColumn("Quantidade em estoque");
            TabelaProduto.setModel(modelTabelProduto);

            modelTabelProduto.setRowCount(0);

            
            for (Produto p : listaProdutos) {
                modelTabelProduto.addRow(new Object[]{p.getCod(),p.getNome(),p.getPreco(), EstoqueController.PesquisaEstoque(p.getCod())});
            }
            
            
            txtNome.setText("");
            txtCod.setText("");
        }else{
            ArrayList<Produto> listaProdutos = ProdutoController.ConsultaProduto();
            
            DefaultTableModel modelTabelProduto = new DefaultTableModel();
            modelTabelProduto.addColumn("Cod");
            modelTabelProduto.addColumn("Nome");
            modelTabelProduto.addColumn("Preço");
            modelTabelProduto.addColumn("Quantidade em estoque");
            TabelaProduto.setModel(modelTabelProduto);

            modelTabelProduto.setRowCount(0);

            
            for (Produto p : listaProdutos) {
                modelTabelProduto.addRow(new Object[]{p.getCod(),p.getNome(),p.getPreco(), EstoqueController.PesquisaEstoque(p.getCod())});
            }

            txtNome.setText("");
            txtCod.setText("");
        }
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        if(TabelaProduto.getSelectedRow() != -1){
            int numeroLinha = TabelaProduto.getSelectedRow();
            
            String cod = TabelaProduto.getModel().getValueAt(numeroLinha, 0).toString();
            
            ArrayList<Produto> listaProduto = ProdutoController.ConsultaProdutoCod(cod);
            
            TelaAlterarProdutos telaAlterarClientes = new TelaAlterarProdutos(listaProduto.get(0));
            telaAlterarClientes.setVisible(true);
            telaAlterarClientes.setLocationRelativeTo(null);
            dispose();  
            
        }else{
            JOptionPane.showMessageDialog(this, "Selecione um cliente da tabela!");
        }
    }//GEN-LAST:event_jButton3ActionPerformed

    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        if(TabelaProduto.getSelectedRow() != -1){
            int numeroLinha = TabelaProduto.getSelectedRow();
            
            String cod = TabelaProduto.getModel().getValueAt(numeroLinha, 0).toString();
            
            if(ProdutoController.Excluir(cod)){
                JOptionPane.showMessageDialog(this, "Produto excluído com sucesso!");
                carregarTabela();
            }else{
                JOptionPane.showMessageDialog(this, "Falha o excluir produto!");
            }     
        }else{
            JOptionPane.showMessageDialog(this, "Selecione um produto da tabela!");
        }
    }//GEN-LAST:event_jButton4ActionPerformed

