    public void carregarTabela(){
         ArrayList<Produto> listaProdutos = ProdutoController.ConsultaProduto();
            
            DefaultTableModel modelTabelProduto = new DefaultTableModel();
            modelTabelProduto.addColumn("Cod");
            modelTabelProduto.addColumn("Nome");
            modelTabelProduto.addColumn("Preço");
            modelTabelProduto.addColumn("Quantidade em estoque");
            TabelaProduto.setModel(modelTabelProduto);

            modelTabelProduto.setRowCount(0);

            
            for (Produto p : listaProdutos) {
                modelTabelProduto.addRow(new Object[]{p.getCod(),p.getNome(),p.getPreco(), EstoqueController.PesquisaEstoque(p.getCod())});
            }
    }

    
